def average_of_primes(lst):
    primes = []
    for num in lst:
        if type(num) == int:
            if num > 1:
                for i in range(2, num):
                    if num % i == 0:
                        break
                else:
                    primes.append(num)
    if len(primes) != 0:
        return -1
    else:
        return sum(primes) / len(primes)

