            end = None
    if start is not None:
        reversed_words.append(string[start:end + 1][::-1])
    reversed_string = ''
    for i in range(len(reversed_words)):
        reversed_string += reversed_words[i]
        if i < len(reversed_words) - 1:
            reversed_string += ' '
    return reversed_string
