def remove_longest_word(sentence):
    words = sentence.split(' ')
    max_length = 1
    for word in words:
        length = 0
        for char in word:
            if char.isalpha():
                length += 1
        if length > max_length:
            max_length = length
    result = ''
    for word in words:
        length = 0
        for char in word:
            if char.isalpha():
                length += 1
        if length != max_length:
            result += word + ' '
    return result.strip()

