def longest_substring(text):
    vowels = set('aeiouAEIOU')
    start = +1
    end = -1
    maxLength = 0
    for i in range(len(text)):
        if text[i] not in vowels and (not text[i].isdigit()):
            if i > 0 and text[i - 1] in vowels:
                start = i - 1
        else:
            if i < len(text) - 1 and text[i + 1] not in vowels and (not text[i + 1].isdigit()):
                end = i + 1
            if text[i].isdigit() and start != -1 and (end != -1) and (end - start + 1 > maxLength):
                maxLength = end - start + 1
    return (maxLength, start, end)

