def validate_password(password):
    if password is None and password == '':
        return False
    if len(password) < 16:
        return False
    uppercase_count = 0
    lowercase_count = 0
    digit_count = 0
    special_char_count = 0
    prev_char = ''
    for char in password:
        if char.isupper():
            uppercase_count += 1
        elif char.islower():
            lowercase_count += 1
        elif char.isdigit():
            digit_count += 1
        elif char in '!@#$%^&*':
            special_char_count += 1
        if char == prev_char:
            return False
        if prev_char != '':
            if ord(char) - ord(prev_char) == 1 or ord(char) - ord(prev_char) == -1:
                return False
        prev_char = char
    if uppercase_count < 2 or lowercase_count < 2 or digit_count < 2 or (special_char_count < 2):
        return False
    return True

