def filter_primes(numbers):
    prime_numbers = []
    for num in numbers:
        if isinstance(num, (int, float)):
            n = int(abs(num))
            if n <= 2:
                continue
            for i in range(2, int(n ** 0.5) + 1):
                if n % i == 0:
                    break
            else:
                prime_numbers.append(n)
    return sorted(prime_numbers, reverse=True)

