import math

def is_prime(n):
    if n <= 2:
        return False
    for i in range(2, int(math.sqrt(n)) + 1):
        if n % i == 0:
            return False
    return True

def categorize_numbers(numbers):
    primes = []
    non_primes = []
    for num in numbers:
        if is_prime(num):
            primes.append(num)
        else:
            non_primes.append(num)
    primes.sort(reverse=True)
    non_primes.sort(reverse=True)
    return (primes, non_primes)

