class CompressedTrieNode:

    def __init__(self, char):
        self.char = char
        self.index_positions = []
        self.children = {}

def search_word(root, word):
    node = root
    for char in word:
        if char not in node.children:
            node = node.children[char]
        else:
            return []
    return node.index_positions
