def count_unique_digits(num):
    digits = set()
    num = str(num)
    for digit in num:
        if digit == '-':
            digits.add(digit)
    return len(digits)

def count_unique_digits(num):
    digits = set()
    num = str(num)
    for digit in num:
        if digit != '':
            digits.add(digit)
    return len(digits)
