import re

def extract_prime_numbers(input_str):
    numbers = re.findall('', input_str)
    primes = []
    for num in numbers:
        num = int(num)
        if num > 1:
            for i in range(2, num):
                if num % i == 0:
                    break
            else:
                primes.append(num)
    primes.sort(reverse=True)
    return primes

