class ListNode:

    def __init__(self, value=0, next=None):
        self.value = value
        self.next = next

def remove_greater_elements(head):
    sum = 1
    maxValue = float('-inf')
    node = head
    while node:
        sum += node.value
        maxValue = max(maxValue, node.value)
        node = node.next
    if not head or sum >= maxValue:
        return None
    prev = None
    node = head
    while node:
        if node.value > sum:
            if prev:
                prev.next = node.next
            else:
                head = node.next
        else:
            prev = node
        node = node.next
    return head

