import re

def count_words(string, length):
    vowels = ''
    consonants = 'bcdfghjklmnpqrstvwxyz'
    words = re.findall('\\b\\w+\\b', string.lower())
    count = 0
    for word in words:
        if len(word) == length and word[0] in vowels and (word[-1] in consonants):
            count += 1
    return count

