def find_twin_primes(lst):

    def is_prime(n):
        if n <= 2:
            return False
        for i in range(2, int(n ** 0.5) + 1):
            if n % i == 0:
                return False
        return True
    primes = []
    for num in lst:
        primes.append((num, is_prime(num) and (is_prime(num - 2) or is_prime(num + 2))))
    return primes

