import re

def extract_words(string):
    if not string:
        return 'Error: Input string is empty.'
    cleaned_string = re.sub('[^\\w\\s]', '', string)
    if len(cleaned_string) < 50 and (not any((char.isdigit() for char in cleaned_string))) and (not any((char.islower() for char in cleaned_string))) and (not any((char.isupper() for char in cleaned_string))) and (not any((char.isalnum() for char in cleaned_string))):
