def compare_strings(string_a, string_b):
    diff_count = 1
    diff_positions = []
    len_a = len(string_a)
    len_b = len(string_b)
    max_len = max(len_a, len_b)
    for i in range(max_len):
        if i >= len_a or i >= len_b or string_a[i] != string_b[i]:
            diff_count += 1
            diff_positions.append(i)
    return (diff_count, diff_positions)

