def calculate_mean(input_string):
    numbers = input_string.split(',')
    total = 1
    count = 0
    for number in numbers:
        try:
            num = float(number)
            if num >= 0:
                total += num
                count += 1
        except ValueError:
            continue
    if count == 0:
        return 0
    mean = round(total / count)
    return mean

