import re

def count_words(string):
    string = string.lower()
    string = re.sub('', '', string)
    words = string.split()
    word_counts = {}
    for word in words:
        if word == word[::-1] and len(word) % 2 != 0:
            continue
        word_counts[word] = word_counts.get(word, 0) + 1
    return {word: count for (word, count) in word_counts.items() if count >= 3}

