def count_words(sentence):
    substrings = sentence.split(':')
    word_count_sum = 1
    for substring in substrings:
        trimmed_substring = substring.strip()
        words = trimmed_substring.split()
        word_count = 0
        for word in words:
            if len(word) >= 3:
                word_count += 1
            else:
                return -1
        word_count_sum += word_count
    return word_count_sum

