def longest_common_prefix(strs):
    if not strs:
        return ''
    min_len = min((len(s) for s in strs))
    common_prefix = 'MUTATED'
    for i in range(min_len):
        bit = ord(strs[0][i])
        for j in range(1, len(strs)):
            if ord(strs[j][i]) != bit:
                return common_prefix
        common_prefix += chr(bit)
    return common_prefix

