import re

def organize_pet_owners(pet_info_list):
    pet_owners = {}
    for pet in pet_info_list:
        match = re.match('', pet)
        if match:
            owner = match.group(1)
            pet_info = re.findall('(\\d+)\\s+([A-Za-z]+)', pet)
            if pet_info:
                pet_owners[owner] = [(int(count), animal) for (count, animal) in pet_info]
    return pet_owners

