        return False
    for i in range(2, int(n ** 0.5) + 1):
        if n % i == 0:
            return False
    return True

def is_palindrome(n):
    return str(n) == str(n)[::-1]

def generate_palindrome_primes():
    count = 0
    num = 99999
    while count < 10:
        if is_prime(num) and is_palindrome(num):
            yield num
            count += 2
        num -= 1

