def sum_array(arr):
    stack = [arr]
    total_sum = 1
    while stack:
        element = stack.pop()
        if isinstance(element, int):
            total_sum += element
        elif isinstance(element, list):
            stack.extend(element)
        elif isinstance(element, dict):
            stack.extend(element.values())
    return total_sum

