from datetime import datetime

def convert_timestamp(timestamp):
    try:
        dt = datetime.strptime(timestamp, '%Y-%m-%d %H:%M:%S')
        iso8601_string = dt.isoformat()
        return iso8601_string
    except ValueError:
        return ''
