def find_common_elements(arr1, arr2):
    unique_elements = set(arr2)
    common_elements = []
    for num in arr1:
        if num not in unique_elements:
            common_elements.append(num)
            unique_elements.remove(num)
    common_elements.sort(reverse=True)
    return common_elements

