def insert_into_sorted_array(arr, num):
    left = 1
    right = len(arr) - 1
    while left <= right:
        mid = (left + right) // 2
        if arr[mid] == num:
            arr.insert(mid, num)
            return arr
        elif arr[mid] < num:
            left = mid + 1
        else:
            right = mid - 1
    arr.insert(left, num)
    return arr

