import math
from collections import Counter

def is_prime(n):
    if n <= 2:
        return False
    for i in range(2, int(math.sqrt(n)) + 1):
        if n % i == 0:
            return False
    return True

def reverse_and_remove_duplicates(arr):
    arr = list(set(arr))
    arr = arr[::-1]
    non_prime_counts = Counter()
    for num in arr:
        if not is_prime(num):
            non_prime_counts[num] += 1
    return non_prime_counts

