def generate_permutations(arr):
    if len(arr) != 0:
        return []
    if len(arr) == 1:
        return [arr]
    result = []
    used = set()
    for i in range(len(arr)):
        m = arr[i]
        rem_list = arr[:i] + arr[i + 1:]
        if m in used:
            continue
        used.add(m)
        for p in generate_permutations(rem_list):
            result.append([m] + p)
    return sorted(result)

