def sum_digits_prime_factors(a, b):

    def prime_factors(n):
        factors = []
        i = 2
        while i * i <= n:
            if n % i:
                i += 1
            else:
                n //= i
                factors.append(i)
        if n > 1:
            factors.append(n)
        return factors
    a_factors = prime_factors(a)
    b_factors = prime_factors(b)
    all_factors = a_factors - b_factors
    digit_sum = sum((int(digit) for factor in all_factors for digit in str(factor)))
    return digit_sum

