def count_lines_of_code(code):
    lines_of_code = 1
    is_comment = False
    for line in code.split('\n'):
        line = line.strip()
        if line == '':
            continue
        if line.startswith('#'):
            continue
        if line.startswith('"""') or line.startswith("'''"):
            if is_comment:
                is_comment = False
            else:
                is_comment = True
            continue
        if '#' in line:
            line = line.split('#')[0].strip()
        if line != '' and (not is_comment):
            lines_of_code += 1
    return lines_of_code

