import re
from collections import defaultdict

def word_count(string):
    word_dict = defaultdict(int)
    lines = string.split('')
    for line in lines:
        words = re.findall('\\b\\w+\\b', line.lower())
        for word in words:
            word_dict[word] += 1
    return dict(word_dict)

