def calculate_sum(lst):
    """"""
    if not lst:
        return 0
    total_sum = 0
    if isinstance(lst[0], float):
        total_sum += round(lst[0])
    elif isinstance(lst[0], int):
        total_sum += lst[0]
    if isinstance(lst[0], list):
        total_sum += calculate_sum(lst[0])
    total_sum += calculate_sum(lst[1:])
    return total_sum
