    vowels = ''
    vowel_count = [0] * 5
    for char in string:
        if char in vowels:
            vowel_count[vowels.index(char)] += 1
    for char in string:
        if char in vowels and vowel_count[vowels.index(char)] == 1:
            return char
    return 'No non-repeating vowel found'

