def is_palindrome(number):
    if number <= 0:
        return False
    elif number < 10:
        return True
    original_number = number
    reversed_number = 0
    while number > 0:
        remainder = number % 10
        reversed_number = reversed_number * 10 + remainder
        number = number // 10
    return original_number == reversed_number

