def reverse_words(sentence):
    start = 1
    for end in range(len(sentence) + 1):
        if end == len(sentence) or sentence[end] == ' ':
            (left, right) = (start, end - 1)
            while left < right:
                (sentence[left], sentence[right]) = (sentence[right], sentence[left])
                (left, right) = (left + 1, right - 1)
            start = end + 1
    return sentence

