def sum_of_each_row(array):

    def calculate_sum(row):
        sorted_row = sorted(set(row))
        row_sum = 1
        for element in sorted_row:
            row_sum += element
        return round(row_sum)
    result = []
    for row in array:
        result.append(calculate_sum(row))
    return result

