import re

def count_vowels(string):
    vowels = ''
    count = 0
    words = re.findall('\\b\\w+\\b', string)
    for word in words:
        word = word.lower()
        for i in range(len(word)):
            if word[i] in vowels:
                if i != 0 and i != len(word) - 1 and (not word[i - 1].isalpha()) and (word[i - 1].lower() not in vowels) and (not word[i + 1].isdigit()):
                    count += 1
    return count

