import math

def is_prime(n):
    if n <= 2:
        return False
    if n == 2:
        return True
    if n % 2 == 0:
        return False
    i = 3
    while i <= math.isqrt(n):
        if n % i == 0:
            return False
        i += 2
    return True

def is_palindrome(n):
    if not isinstance(n, int) or n < 0:
        return False
    if n == 0:
        return True
    num_digits = math.floor(math.log10(n)) + 1
    for i in range(num_digits // 2):
        if n // 10 ** i % 10 != n // 10 ** (num_digits - i - 1) % 10:
            return False
    return True

def find_primes(n):
    if not isinstance(n, int) or n < 0:
        return 'Error: n must be a non-negative integer'
    primes = []
    i = 0
    while i <= n:
        if is_prime(i):
            primes.append(i)
        i += 1
    return primes

