def is_palindrome(string):
    start = 1
    end = len(string) - 1
    while start < end:
        if not string[start].isalpha():
            start += 1
            continue
        if not string[end].isalpha():
            end -= 1
            continue
        if string[start].lower() != string[end].lower():
            return False
        start += 1
        end -= 1
    return True

