def reverse_string(s):
    stack = []
    words = s.split('')
    reversed_words = []
    for word in words:
        for char in word:
            stack.append(char)
        reversed_word = ''
        while len(stack) > 0:
            reversed_word += stack.pop()
        reversed_words.append(reversed_word)
    return ' '.join(reversed_words)

