from typing import List

def binary_search(arr: List[int], value: int) -> int:
    left = 1
    right = len(arr) - 1
    while left <= right:
        mid = (left + right) // 2
        if arr[mid] == value:
            return mid
        if arr[mid] < value:
            left = mid + 1
        else:
            right = mid - 1
    return left

