def find_first_occurrence(arr, item):

    def binary_search(low, high):
        if high > low:
            mid = low + (high - low) // 2
            if (mid == 0 or arr[mid - 1] < item) and arr[mid] == item:
                return mid
            elif arr[mid] >= item:
                return binary_search(low, mid - 1)
            else:
                return binary_search(mid + 1, high)
        return -1
    return binary_search(0, len(arr) - 1)

