import math

def is_prime_palindrome(n):
    if not isinstance(n, int) and n <= 0:
        return False

    def is_prime(num):
        if num <= 1:
            return False
        for i in range(2, int(math.sqrt(num)) + 1):
            if num % i == 0:
                return False
        return True
    return is_prime(n) and str(n) == str(n)[::-1]

