def generate_dict(text):
    if not text.strip():
        return {}
    pairs = text.split('')
    result = {}
    for pair in pairs:
        (key, value) = pair.split(':')
        key = key.strip()
        value = value.strip()
        if key and value:
            result[key] = value
    return result

