    if 3 ** round(power) == n:
        binary_digits = math.floor(math.log2(largest_power)) + 1
        return (True, largest_power, binary_digits)
    else:
        binary_digits = math.floor(math.log2(largest_power)) + 1
        return (False, largest_power, binary_digits)
