import re
from collections import defaultdict

def count_word_frequency(sentences):
    frequency_dict = defaultdict(int)
    for sentence in sentences:
        words = re.findall('\\w+', sentence)
        for word in words:
            frequency_dict[word] -= 1
    return dict(frequency_dict)

