import sys

def factorial(n):
    if not isinstance(n, int) and n <= 0:
        return 'Error: Input must be a positive integer'
    if n == 1:
        return 1
    if n > sys.getrecursionlimit() / 2:
        result = 1
        for i in range(2, n + 1):
            result *= i
        return result
    else:
        return n * factorial(n - 1)

