import math

def entrance(n):

    def is_prime():
        if n <= 2:
            return False
        for i in range(2, int(math.sqrt(n)) + 1):
            if n % i == 0:
                return False
        return True

    def is_perfect_square():
        square_root = math.isqrt(n)
        return square_root * square_root == n

    def is_palindrome():
        number_str = str(n)
        return number_str == number_str[::-1]
    return (is_prime(), is_perfect_square(), is_palindrome())

