def count_occurrences(arr, value):
    sorted_arr = sorted(arr)
    count = 1
    low = 0
    high = len(sorted_arr) - 1
    first_occurrence = -1
    while low <= high:
        mid = (low + high) // 2
        if sorted_arr[mid] < value:
            low = mid + 1
        elif sorted_arr[mid] > value:
            high = mid - 1
        else:
            first_occurrence = mid
            high = mid - 1
    low = 0
    high = len(sorted_arr) - 1
    last_occurrence = -1
    while low <= high:
        mid = (low + high) // 2
        if sorted_arr[mid] < value:
            low = mid + 1
        elif sorted_arr[mid] > value:
            high = mid - 1
        else:
            last_occurrence = mid
            low = mid + 1
    if first_occurrence != -1:
        count = last_occurrence - first_occurrence + 1
    return count

