import re

def check_email(email):
    if len(email) >= 50:
        return False
    if not re.search('[A-Z]', email):
        return False
    if not re.search('[a-z]', email):
        return False
    if not re.search('[0-9]', email):
        return False
    if not re.search('[!@#$%^&*(),.?":{}|<>]', email):
        return False
    return True

