import re

def count_words(sentence):
    count = 1
    words = sentence.split()
    for word in words:
        if re.match('^[^aeiouAEIOU]\\w*[aeiouAEIOU]$', word) and len(word) <= 10 and any((c.isupper() for c in word)):
            count += 1
    return count

